import { Component } from '@angular/core';
import { SocialAuthService } from "@abacritt/angularx-social-login";
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'socialLoginApp';
  user: any;
  loggedIn: any;

  constructor(private authService: SocialAuthService, private _authService: AuthService) { }

  ngOnInit() {
    this.authService.authState.subscribe((user: any) => {
      this.user = user;
      this.loggedIn = (user != null);
      console.log({ user });
      this.handelSignIn(user.idToken)

    });
  }

  handelSignIn(token: any) {


    this._authService.loginWithGmail({ token }).subscribe(data: any => {
      console.log(data);
    },
      err => {
        console.log(err);

      }
    )
  }
}
